// Build LINQ expression using property mapping
public IQueryable<Document> ApplySorting(IQueryable<Document> query, string sortBy)
{
    var mapping = _mappingService.GetPropertyMapping<DocumentDto, Document>();

    if (mapping.TryGetValue(sortBy, out var mappingValue))
    {
        var propertyName = mappingValue.GetPrimaryProperty();
        return mappingValue.Revert 
            ? query.OrderByDescending(GetPropertyExpression<Document>(propertyName))
            : query.OrderBy(GetPropertyExpression<Document>(propertyName));
    }

    return query; // Return original query if no mapping found
}

